function date2iso(date){return date.split("/").reverse().join("-")}function formatInt(num,len){for(var buff=num,size=num.toString().length;len>size;)buff="0"+buff,len--;return buff}function iso2date(iso){return iso.split("-").reverse().join("/")}function jsonDownload(json,option){window.URL=window.URL||window.webkitURL;var json=jsonFormatter(json,option),blob=new Blob([json],{type:"application/octet-stream"});return window.URL.createObjectURL(blob)}function jsonFormatter(json,option){var json=JSON.parse(json);return"compact"==option?"object"==typeof json&&(json=JSON.stringify(json)):(option="2"==option||"4"==option?parseInt(option):"	","object"==typeof json&&(json=JSON.stringify(json,null,option))),json}function string2float(value){var n=0,buffer=new Array;for(i in value)buffer[n++]=parseFloat(value[i]);return buffer}function todayDate(){var d=new Date;return formatInt(d.getDate(),2)+"/"+formatInt(d.getMonth()+1,2)+"/"+d.getFullYear()}function todayISO(){var d=new Date;return d.getFullYear()+"-"+formatInt(d.getMonth(),2)+"-"+formatInt(d.getDate(),2)}function todaytsISO(){var d=new Date;return d.getFullYear()+"-"+formatInt(d.getMonth()+1,2)+"-"+formatInt(d.getDate(),2)+" "+formatInt(d.getHours(),2)+":"+formatInt(d.getMinutes(),2)+":"+formatInt(d.getSeconds(),2)}function tsISO2tsDate(date){var date=date.split(" ");return iso2date(date[0])+" "+date[1]}function tw2date(date){var d=date.split(" "),ts=new Date(Date.parse(d[0]+", "+d[2]+" "+d[1]+" "+d[5]+" "+d[3]+" GMT"+d[4]));return ts.getFullYear()+"-"+formatInt(ts.getMonth()+1,2)+"-"+formatInt(ts.getDate(),2)+" "+formatInt(ts.getHours(),2)+":"+formatInt(ts.getMinutes(),2)+":"+formatInt(ts.getSeconds(),2)}
